import React, { useState, useEffect } from 'react';

export default function Calculator1(props) {
    let n1 = parseFloat(props.num1) || 0;
    let n2 = parseFloat(props.num2) || 0;

    const [results, setResults] = useState({});

    function Addition() {
        return n1 + n2;
    }
    function Subtraction() {
        return n1 - n2;
    }
    function Multiplication() {
        return n1 * n2;
    }
    function Division() {
        return n2 !== 0 ? n1 / n2 : 'Cannot divide by zero';
    }

    useEffect(() => {
        setResults({
            addition: Addition(),
            subtraction: Subtraction(),
            multiplication: Multiplication(),
            division: Division(),
        });
    }, [props.num1, props.num2]);

    return (
        <>
        <h2>Calculator (State Managed)</h2>
        <p>Numbers: {n1} and {n2}</p>
        <p>Addition : {results.addition}</p>
        <p>Subtraction : {results.subtraction}</p>
        <p>Multiplication : {results.multiplication}</p>
        <p>Division : {results.division}</p>
        </>
    );
}
